<#
#   Intent: Ran from Collection Commander. Create scheduled task on remote machines. Task will run Check-SCCMHealth periodically.
#   Date: 26-Feb-25
#   Author: Matthew Wurtz
#>>

# Uses Base64 encoded string of the script for ease of use 
$encodedCommand = ""

$action = New-ScheduledTaskAction -Execute "powershell.exe" `
    -Argument "-NoProfile -ExecutionPolicy Bypass -EncodedCommand $encodedCommand"

$trigger = New-ScheduledTaskTrigger -Weekly -DaysOfWeek Monday 

$settings = New-ScheduledTaskSettingsSet -WakeToRun

$principal = New-ScheduledTaskPrincipal -UserId "NT AUTHORITY\SYSTEM" -LogonType ServiceAccount

$description = "Will download, store locally newest update to Check-SCCMHealth, `
    and then run it. Output will be stored locally until retrieved by server."


Register-ScheduledTask -TaskName "Check-SCCMHealthTask" `
                       -Action $action `
                       -Trigger $trigger `
                       -settings $settings `
                       -Principal $principal `
                       -Description $description
